import React, { useEffect, useState } from "react";
import axios from "axios";

const Comments = () => {
  const [comments, setComments] = useState<any[]>([]);
  const [text, setText] = useState("");
  const [editId, setEditId] = useState<number | null>(null);

  const fetchComments = async () => {
    const res = await axios.get("https://localhost:5001/api/Comments");
    setComments(res.data);
  };

  useEffect(() => {
    fetchComments();
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("text", text);

    try {
      if (editId !== null) {
        await axios.put(`https://localhost:5001/api/Comments/${editId}`, formData);
      } else {
        await axios.post("https://localhost:5001/api/Comments", formData, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
      }
      setText("");
      setEditId(null);
      fetchComments();
    } catch (err) {
      alert("Failed to save comment");
    }
  };

  const handleDelete = async (id: number) => {
    await axios.delete(`https://localhost:5001/api/Comments/${id}`);
    fetchComments();
  };

  const handleEdit = (comment: any) => {
    setText(comment.text);
    setEditId(comment.id);
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Comments</h2>
      <form onSubmit={handleSubmit} className="mb-6 space-y-2">
        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Write a comment"
          className="w-full border p-2 rounded"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
          {editId !== null ? "Update" : "Add"} Comment
        </button>
      </form>

      <ul className="space-y-2">
        {comments.map((c) => (
          <li key={c.id} className="border p-3 rounded flex justify-between items-center">
            <div>{c.text}</div>
            <div className="space-x-2">
              <button onClick={() => handleEdit(c)} className="bg-yellow-400 px-3 py-1 rounded">Edit</button>
              <button onClick={() => handleDelete(c.id)} className="bg-red-500 text-white px-3 py-1 rounded">Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Comments;
